INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1567872, 'Candida albicans', 'Mumps virus strain B level jeryl lynn live antigen', 'Moderate', 'Reactivity to diagnostic skin test antigens may be temporarily depressed in patients who have recently been vaccinated with live virus vaccines.', 'DDInter', 1767317477, 'Clinicians should be aware of the potential for falsely insignificant or false-negative results when performing delayed-hypersensitivity skin testing on patients who have recently received live virus vaccines.  Diagnostic skin test antigens should generally be administered before or 4 to 6 weeks after immunization.  It is recommended to refer to local guidelines or prescribing information.', 'Antagonism', 'Clinicians should be aware of the potential for falsely insignificant or false-negative results when performing delayed-hypersensitivity skin testing on patients who have recently received live virus vaccines.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1567873, 'Human immunoglobulin G (intramuscular)', 'Mumps virus strain B level jeryl lynn live antigen', 'Moderate', 'The passive transfer of antibodies present in immune globulin preparations may interfere with the immune response to live vaccines.', 'DDInter', 1767317477, 'Varicella virus vaccine should not be given for at least 5 months, and other live vaccines should not be given for at least 3 months, after administration of immune globulin.  If such vaccinations are given shortly before or after immune globulin therapy, a revaccination may be necessary.  Some experts have recommended that administration of measles virus vaccine be deferred for at least 8 months following human intravenous immune globulin therapy for immunodeficiency disease; 8 to 10 months following treatment for immune thrombocytopenic purpura (ITP); and at least 11 months following treatment for Kawasaki syndrome.  In the event that immune globulin and measles virus vaccine must be administered concurrently, it is advisable to separate the injection sites by as much as possible and to administer an additional dose of vaccine at the appropriate specified interval.  An additional dose of live measles, mumps, or rubella vaccine is usually not needed if the vaccine was administered at least two weeks prior to immune globulin administration.  Hepatitis B immune globulin has been reported to not interfere with measles vaccination, although data are limited.', 'Antagonism', 'Varicella virus vaccine should not be given for at least 5 months, and other live vaccines should not be given for at least 3 months, after administration of immune globulin.', NULL, NULL, 'Moderate', 'pharmacodynamic');
