    ) VALUES (1567943, 'Anisindione', 'Rofecoxib', 'Moderate', 'Rofecoxib may potentiate the hypoprothrombinemic effect of warfarin and other oral anticoagulants as determined by the INR.', NULL, 'Patients should be closely monitored during concomitant therapy with oral anticoagulants and rofecoxib.', NULL, 'Patients should be closely monitored during concomitant therapy with oral anticoagulants and rofecoxib.  The INR should be checked frequently and anticoagulant dosage adjusted accordingly, particularly following initiation, discontinuation or change of dosage of rofecoxib in patients who are stabilized on their anticoagulant regimen.  Patients should be advised to promptly report any signs of unusual bleeding or bruising to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, vaginal bleeding, nosebleeds, bleeding of gums from brushing, red or brown urine, or red or black stools.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
