    ) VALUES (1568099, 'Boceprevir', 'Lopinavir', 'Major', 'Coadministration of boceprevir with cobicistat-boosted or ritonavir-boosted regimens of atazanavir, darunavir, or lopinavir may result in decreased plasma concentrations of both boceprevir and the protease inhibitors.  The mechanism of interaction has not been described.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiviral drug levels, the use of boceprevir in combination with ritonavir-boosted or cobicistat-boosted regimens of atazanavir, darunavir, or lopinavir is not recommended.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiviral drug levels, the use of boceprevir in combination with ritonavir-boosted or cobicistat-boosted regimens of atazanavir, darunavir, or lopinavir is not recommended.  Moreover, the safety and efficacy of boceprevir has not been established in the HCV/HIV coinfected population.', 'Others', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
