    ) VALUES (1568163, 'Duvelisib', 'Lopinavir', 'Minor', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of lopinavir, which is primarily metabolized by the isoenzyme.', NULL, 'The magnitude and clinical significance of this interaction are unknown, particularly when in the presence of ritonavir as a pharmacokinetic booster.', NULL, 'The magnitude and clinical significance of this interaction are unknown, particularly when in the presence of ritonavir as a pharmacokinetic booster.', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
