    ) VALUES (1568167, 'Eltrombopag', 'Lopinavir', 'Moderate', 'Coadministration with lopinavir-ritonavir has been shown to modestly decrease the plasma concentrations of eltrombopag.', NULL, 'No specific dosage adjustment is recommended when eltrombopag is used with lopinavir-ritonavir.', NULL, 'No specific dosage adjustment is recommended when eltrombopag is used with lopinavir-ritonavir.  However, platelet count should be more closely monitored following initiation or discontinuation of lopinavir-ritonavir therapy, and the eltrombopag dosage adjusted as necessary.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
