INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1568224, 'Idelalisib', 'Lopinavir', 'Minor', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of lopinavir, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'The magnitude and clinical significance of this interaction are unknown, particularly when in the presence of ritonavir as a pharmacokinetic booster.', 'Metabolism', 'The magnitude and clinical significance of this interaction are unknown, particularly when in the presence of ritonavir as a pharmacokinetic booster.', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1568225, 'Iloperidone', 'Lopinavir', 'Major', 'Iloperidone may cause dose-related prolongation of the QT interval.  Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 1767317477, 'Coadministration of iloperidone with other drugs that can prolong the QT interval should generally be avoided.  Caution and clinical monitoring are recommended if concomitant use is required.  Patients should have regular ECGs and be monitored for arrhythmias when QT interval is prolonged.  Persistent QTc measurements exceeding 500 msec will require suspension of iloperidone therapy and immediate action to correct any concomitant risk factors before resuming treatment.  Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.  In addition, if combination therapy with agents with anticholinergic properties is required, caution is advised, particularly in the elderly and those with underlying organic brain disease.  Patients should be advised to notify their physician promptly if they experience potential symptoms of anticholinergic intoxication such as abdominal pain, fever, heat intolerance, blurred vision, confusion, and/or hallucinations.  Ambulatory patients should be counseled to avoid activities requiring mental alertness until they know how these agents affect them.  A reduction in anticholinergic dosages may be necessary if excessive adverse effects develop.', 'Synergism', 'Coadministration of iloperidone with other drugs that can prolong the QT interval should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
