INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1568291, 'Metoprolol', 'Lopinavir', 'Moderate', 'Lopinavir in combination with ritonavir has been shown to prolong the PR interval of the electrocardiogram in some patients.  Theoretically, coadministration with other agents that prolong the PR interval (e.g., beta blockers, calcium channel blockers, atazanavir, digoxin, lacosamide, mefloquine) may result in additive effects and increased risk of conduction disturbances and atrioventricular block.', 'DDInter', 1767317477, 'Caution is advised if lopinavir-ritonavir is used concomitantly with other agents that prolong the PR interval, particularly those that are metabolized by CYP450 3A4 (e.g., calcium channel blockers) because lopinavir-ritonavir is a potent inhibitor of the isoenzyme.  The elderly as well as patients with underlying structural heart disease, preexisting conduction system abnormalities, ischemic heart disease, or cardiomyopathies may be at increased risk for developing cardiac conduction disturbances and atrioventricular block associated with the use of lopinavir-ritonavir.  Patients should be advised to notify their physician if they experience dizziness, lightheadedness, fainting, or irregular heartbeat.', 'Synergism', 'Caution is advised if lopinavir-ritonavir is used concomitantly with other agents that prolong the PR interval, particularly those that are metabolized by CYP450 3A4 (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1568292, 'Metronidazole', 'Lopinavir', 'Moderate', 'QT prolongation has been reported with metronidazole, particularly when administered with drugs that have the potential for prolonging the QT interval.  This may increase the risk of ventricular arrhythmias associated with QT prolongation including torsade de pointes and sudden death.', 'DDInter', 1767317477, 'Caution is recommended when metronidazole is used concomitantly with agents known to cause QT prolongation.  Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Caution is recommended when metronidazole is used concomitantly with agents known to cause QT prolongation.', NULL, NULL, 'Moderate', 'pharmacodynamic');
