INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1568360, 'Primidone', 'Lopinavir', 'Major', 'Coadministration of lopinavir-ritonavir with potent inducers of CYP450 3A4 such as phenobarbital and primidone (metabolized in vivo to phenobarbital) may decrease the plasma concentrations of lopinavir, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, caution is advised if lopinavir-ritonavir is prescribed with phenobarbital or primidone.  Once-daily administration of lopinavir-ritonavir should be avoided when used concomitantly with these agents.  Close clinical and laboratory monitoring of antiretroviral response is recommended.  An increase in the dosage of lopinavir-ritonavir may be necessary, although dosage adjustment has not been evaluated in clinical studies.', 'Metabolism', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, caution is advised if lopinavir-ritonavir is prescribed with phenobarbital or primidone.', NULL, NULL, 'Major', 'pharmacodynamic');
