INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1568391, 'Saquinavir', 'Lopinavir', 'Major', 'Both saquinavir and lopinavir have been associated with prolongation of the QT interval when coadministered with ritonavir as a pharmacokinetic booster.  Theoretically, combining these medications may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 1767317477, 'The product labeling for saquinavir and lopinavir-ritonavir both recommend that these medications not be used with other agents that can prolong the QT interval.  Whether they should be used with each other is unknown, but coadministration may increase the risk of QT and PR prolongation and should preferably be avoided if possible.  If concomitant use is necessary, the recommended dosages are saquinavir 1000 mg twice daily and lopinavir-ritonavir 400 mg-100 mg twice daily.  Once-daily dosing of lopinavir-ritonavir has not been studied for use in combination with saquinavir.  Patients should be advised to notify their physician if they experience dizziness, lightheadedness, fainting, palpitations, syncope, and/or irregular heartbeat.', 'Synergism', 'The product labeling for saquinavir and lopinavir-ritonavir both recommend that these medications not be used with other agents that can prolong the QT interval.', NULL, NULL, 'Major', 'pharmacodynamic');
