INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1568399, 'Siponimod', 'Lopinavir', 'Major', 'Due to its significant bradycardic effects, the risk of QT prolongation and torsade de pointes arrhythmia may be increased during initiation of siponimod treatment in patients receiving drugs that prolong the QT interval.', 'DDInter', 1767317477, 'Siponimod has not been studied in patients receiving drugs that can prolong the QT interval.  Because bradycardia and AV block are recognized risk factors for QT prolongation and torsade de pointes arrhythmia, treatment with siponimod should generally not be initiated in patients who are concurrently treated with QT prolonging drugs with known arrhythmogenic properties.  Advice from a cardiologist should be sought if treatment with siponimod is considered in patients on concurrent therapy with QT prolonging drugs with a known risk of torsades de pointes or drugs that slow heart rate or AV conduction.', 'Synergism', 'Siponimod has not been studied in patients receiving drugs that can prolong the QT interval.', NULL, NULL, 'Major', 'pharmacodynamic');
