INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1568738, 'Benzhydrocodone', 'Kava', 'Moderate', 'Central nervous system- and/or respiratory-depressant effects may be additively or synergistically increased in patients taking multiple drugs that cause these effects, especially in elderly or debilitated patients.', 'DDInter', 1767317477, 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.  Cautious dosage titration may be required, particularly at treatment initiation.  Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination until they know how these agents affect them, and to notify their physician if they experience excessive or prolonged CNS effects that interfere with their normal activities.', 'Synergism', 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1568739, 'Benzhydrocodone', 'Ketamine', 'Major', 'Coadministration of ketamine with opioid analgesics, benzodiazepines, or other central nervous system (CNS) depressants, including alcohol, may result in profound sedation, respiratory depression, coma, and death.  Concomitant use of ketamine with other CNS depressants can potentiate CNS depression and/or increase the risk of developing respiratory depression.  In addition, opioid analgesics, barbiturates, and benzodiazepines may prolong the time to complete recovery from anesthesia.', 'DDInter', 1767317477, 'During concomitant use of ketamine with opioid analgesics, benzodiazepines, or other central nervous system (CNS) depressants, including alcohol, close monitoring of neurologic status and respiratory parameters, including respiratory rate and pulse oximetry, is recommended.  Dosage adjustments should be considered according to the patient''s clinical situation.  Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination until they know how these agents affect them, and to notify their physician if they experience excessive or prolonged CNS effects that interfere with their normal activities.', 'Synergism', 'During concomitant use of ketamine with opioid analgesics, benzodiazepines, or other central nervous system (CNS) depressants, including alcohol, close monitoring of neurologic status and respiratory parameters, including respiratory rate and pulse oximetry, is recommended.', NULL, NULL, 'Major', 'pharmacodynamic');
