INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1568769, 'Benzhydrocodone', 'Mebrofenin', 'Moderate', 'Prior administration of opioids may delay transit of Technetium Tc 99m mebrofenin due to opioid-induced contraction of the distal common bile duct, which may result in nonvisualization.', 'DDInter', 1767317477, 'Nonvisualization may occur in patients who have been receiving opioids prior to cholescintigraphy.', 'Others', 'Nonvisualization may occur in patients who have been receiving opioids prior to cholescintigraphy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
