INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1568833, 'Benzhydrocodone', 'Osilodrostat', 'Moderate', 'Coadministration with osilodrostat may increase the plasma concentrations of drugs that are metabolized by CYP450 1A2, 2C19, 2D6, and/or 3A4.  Osilodrostat has been shown to be a moderate inhibitor of CYP450 1A2, a mild to borderline moderate inhibitor of CYP450 2C19, and a weak inhibitor of CYP450 2D6 and 3A4.', 'DDInter', 1767317477, 'Caution is advised when osilodrostat is used concurrently with drugs that are substrates of CYP450 1A2, 2C19, 2D6 and/or 3A4, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever osilodrostat is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when osilodrostat is used concurrently with drugs that are substrates of CYP450 1A2, 2C19, 2D6 and/or 3A4, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
