INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1568985, 'Upadacitinib', 'Benzhydrocodone', 'Moderate', 'Coadministration of Janus kinase (JAK) inhibitors with corticosteroids, nonsteroidal anti-inflammatory drugs (NSAIDs), and/or opioids may increase the risk of gastrointestinal (GI) perforation.', 'DDInter', 1767317477, 'Caution is recommended when using JAK inhibitors in patients with a history of peptic ulceration or diverticular disease and in patients receiving concomitant treatment with drugs associated with an increased risk of GI perforation such as corticosteroids, NSAIDs, and opioids.  Patients should be advised to contact their healthcare provider if they experience signs and symptoms of GI perforation such as severe abdominal pain, fever, chills, nausea, or vomiting.', 'Synergism', 'Caution is recommended when using JAK inhibitors in patients with a history of peptic ulceration or diverticular disease and in patients receiving concomitant treatment with drugs associated with an increased risk of GI perforation such as corticosteroids, NSAIDs, and opioids.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1568986, 'Ustekinumab', 'Benzhydrocodone', 'Moderate', 'Plasma concentrations of drugs that are CYP450 substrates may decrease following the initiation of interleukin (IL) inhibitors, tumor necrosis factor (TNF) blockers, or interferon (IFN) inhibitors in patients with chronic inflammatory diseases.  Because the formation of hepatic CYP450 enzymes is down-regulated during infection and chronic inflammation by increased levels of certain cytokines (e.g., interleukins-1, -6, and -10; tumor necrosis factor alpha; interferons), treatment targeting these cytokines may restore or normalize CYP450 enzyme levels resulting in increased metabolism of these drugs.', 'DDInter', 1767317477, 'Caution is advised when treatments targeting cytokines such as interleukins, tumor necrosis factors, or interferons are prescribed to patients receiving concomitant drugs that are CYP450 substrates, particularly those with narrow therapeutic ranges (e.g., antiarrhythmics, anticonvulsants, immunosuppressants, theophylline) or sensitive substrates where decreases in plasma levels may be significant or undesirable (e.g., oral contraceptives, statins, benzodiazepines, opioids).  Clinical and/or laboratory monitoring should be considered following the initiation or withdrawal of such treatments, and the dosage(s) of the CYP450 substrate(s) adjusted accordingly.  Clinicians should note that the effects of IL inhibitors, TNF blockers, and IFN inhibitors on CYP450 activities may persist for several weeks after stopping therapy.', 'Metabolism', 'Caution is advised when treatments targeting cytokines such as interleukins, tumor necrosis factors, or interferons are prescribed to patients receiving concomitant drugs that are CYP450 substrates, particularly those with narrow therapeutic ranges (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
