INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1569021, 'Codeine', 'St. John''s Wort', 'Moderate', 'Coadministration of codeine or dihydrocodeine with CYP450 3A4 inducers such as St. John''s Wort may result in lower codeine or dihydrocodeine plasma concentrations, higher levels of the inactive metabolite norcodeine or dihydronorcodeine, and less metabolism via CYP450 2D6, resulting in lower morphine or dihydromorphine levels, respectively.', 'DDInter', 1767317477, 'Caution and clinical monitoring are advised if codeine or dihydrocodeine are to be used in combination with St. John''s Wort, particularly when it is added to or withdrawn from therapy.  Dosage adjustments may be required.  Some manufacturers of products containing codeine recommend against using codeine concomitantly with CYP450 3A4 inducers.  Patients should be advised to monitor for symptoms of serotonin excess or serotonin syndrome.  If serotonin syndrome develops or is suspected during the course of therapy, all serotonergic agents should be discontinued immediately, and supportive care rendered as necessary.  Patients should also be advised to avoid hazardous activities requiring complete mental alertness and motor coordination until they know how these medications affect them.', 'Metabolism', 'Caution and clinical monitoring are advised if codeine or dihydrocodeine are to be used in combination with St.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1569022, 'Codeine', 'Telotristat ethyl', 'Moderate', 'Coadministration of codeine with potent or moderate CYP450 3A4 inducers may result in lower codeine plasma concentrations, higher levels of the inactive metabolite norcodeine, and less metabolism via CYP450 2D6, resulting in lower morphine levels.', 'DDInter', 1767317477, 'The potential loss of efficacy of codeine or dihydrocodeine and onset of opioid withdrawal symptoms should be considered when used in combination with a potent or moderate CYP450 3A4 inducer.  Alternative agents with no or minimal CYP450 3A4 induction potential are recommended whenever possible.  Some manufacturers of products containing codeine advise against the concomitant use of codeine with CYP450 3A4 inducers.  If concomitant use is considered necessary, caution and close clinical and laboratory monitoring are recommended.  Dosage adjustments may also be required whenever a CYP450 3A4 inducer is added to or withdrawn from therapy.  Following discontinuation of a CYP450 3A4 inducer, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', 'Metabolism', 'The potential loss of efficacy of codeine or dihydrocodeine and onset of opioid withdrawal symptoms should be considered when used in combination with a potent or moderate CYP450 3A4 inducer.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1569023, 'Codeine', 'Upadacitinib', 'Moderate', 'Coadministration of Janus kinase (JAK) inhibitors with corticosteroids, nonsteroidal anti-inflammatory drugs (NSAIDs), and/or opioids may increase the risk of gastrointestinal (GI) perforation.', 'DDInter', 1767317477, 'Caution is recommended when using JAK inhibitors in patients with a history of peptic ulceration or diverticular disease and in patients receiving concomitant treatment with drugs associated with an increased risk of GI perforation such as corticosteroids, NSAIDs, and opioids.  Patients should be advised to contact their healthcare provider if they experience signs and symptoms of GI perforation such as severe abdominal pain, fever, chills, nausea, or vomiting.', 'Synergism', 'Caution is recommended when using JAK inhibitors in patients with a history of peptic ulceration or diverticular disease and in patients receiving concomitant treatment with drugs associated with an increased risk of GI perforation such as corticosteroids, NSAIDs, and opioids.', NULL, NULL, 'Moderate', 'pharmacodynamic');
