INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1569052, 'Ibuprofen', 'Tafasitamab', 'Moderate', 'Coadministration of tafasitamab with drugs that can affect hemostasis such as anticoagulants, platelet inhibitors, thrombin inhibitors, thrombolytic agents, dextrans, and nonsteroidal anti-inflammatory drugs (NSAIDs) may increase the risk of bleeding.', 'DDInter', 1767317477, 'When possible, consideration should be given to withholding concomitant medications that may increase bleeding risk in patients treated with tafasitamab.  Close clinical and laboratory monitoring for bleeding complications is recommended if concomitant use is unavoidable.  Refer to the product labeling for guidance on frequency of laboratory monitoring and dose modifications or withholding recommendations for platelet counts below 50,000/mcL.  Patients should be advised to promptly report any signs of unusual bleeding or bruising to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, red or brown urine, or red or black stools.', 'Synergism', 'When possible, consideration should be given to withholding concomitant medications that may increase bleeding risk in patients treated with tafasitamab.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1569053, 'Ibuprofen', 'Upadacitinib', 'Moderate', 'Coadministration of Janus kinase (JAK) inhibitors with corticosteroids, nonsteroidal anti-inflammatory drugs (NSAIDs), and/or opioids may increase the risk of gastrointestinal (GI) perforation.', 'DDInter', 1767317477, 'Caution is recommended when using JAK inhibitors in patients with a history of peptic ulceration or diverticular disease and in patients receiving concomitant treatment with drugs associated with an increased risk of GI perforation such as corticosteroids, NSAIDs, and opioids.  Patients should be advised to contact their healthcare provider if they experience signs and symptoms of GI perforation such as severe abdominal pain, fever, chills, nausea, or vomiting.', 'Synergism', 'Caution is recommended when using JAK inhibitors in patients with a history of peptic ulceration or diverticular disease and in patients receiving concomitant treatment with drugs associated with an increased risk of GI perforation such as corticosteroids, NSAIDs, and opioids.', NULL, NULL, 'Moderate', 'pharmacodynamic');
