INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1569071, 'Amantadine', 'Phenyltoloxamine', 'Moderate', 'The anticholinergic-like adverse effects of amantadine may be potentiated by agents with anticholinergic properties such as antihistamines, antispasmodics, class IA antiarrhythmics, neuroleptics, phenothiazines, skeletal muscle relaxants, and tricyclic antidepressants.', 'DDInter', 1767317477, 'Caution is advised when amantadine is used in combination with anticholinergic agents, particularly in the elderly and those with underlying organic brain disease, who tend to be more sensitive to the central anticholinergic effects of these drugs and in whom toxicity symptoms may be easily overlooked.  Patients should be advised to notify their physician promptly if they experience potential symptoms of anticholinergic intoxication such as abdominal pain, fever, heat intolerance, blurred vision, confusion, and/or hallucinations.  Ambulatory patients should be counseled to avoid activities requiring mental alertness until they know how these agents affect them.  A reduction in anticholinergic and/or amantadine dosage may be necessary if excessive adverse effects develop.', 'Synergism', 'Caution is advised when amantadine is used in combination with anticholinergic agents, particularly in the elderly and those with underlying organic brain disease, who tend to be more sensitive to the central anticholinergic effects of these drugs and in whom toxicity symptoms may be easily overlooked.', NULL, NULL, 'Moderate', 'pharmacodynamic');
