INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1569086, 'Brimonidine (ophthalmic)', 'Phenyltoloxamine', 'Moderate', 'Topically administered alpha-2 adrenergic receptor agonists such as apraclonidine and brimonidine are systemically absorbed, with the potential for producing rare but clinically significant systemic effects.  Although the interaction has not been specifically studied, the possibility of an additive or potentiating effect with central nervous system (CNS) depressants such as alcohol, barbiturates, opiates, anxiolytics, sedatives, and anesthetics should be considered.  Additive hypotensive effects and orthostasis may also occur with some CNS depressants and other agents that have these effects, particularly during initial dosing and/or parenteral administration.', 'DDInter', 1767317477, 'Patients receiving topical alpha-2 adrenergic receptor agonists in combination with agents that can cause CNS depression should be made aware of the potential for increased adverse effects such as drowsiness, dizziness, lightheadedness and confusion, and counseled to avoid activities requiring mental alertness until they know how these agents affect them.  Patients should also avoid rising abruptly from a sitting or recumbent position and notify their physician if they experience orthostasis or tachycardia.', 'Synergism', 'Patients receiving topical alpha-2 adrenergic receptor agonists in combination with agents that can cause CNS depression should be made aware of the potential for increased adverse effects such as drowsiness, dizziness, lightheadedness and confusion, and counseled to avoid activities requiring mental alertness until they know how these agents affect them.', NULL, NULL, 'Moderate', 'pharmacodynamic');
