INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1569844, 'Diamorphine', 'Metformin', 'Moderate', 'Morphine is a cationic drug and theoretically may decrease the excretion of metformin by competing for renal tubular transport.', 'DDInter', 1767317477, 'If morphine and metformin must be used together, particularly slow and cautious titration of metformin dosage is recommended.  The maximal dose of metformin probably also should  be reduced until further information about this interaction is available.  Patients should be advised to monitor their blood glucose and to promptly notify their physician if they experience possible signs of lactic acidosis such as malaise, myalgia, respiratory distress, hyperventilation, slow or irregular heartbeat, somnolence, abdominal upset, or other unusual symptoms.', 'Excretion', 'If morphine and metformin must be used together, particularly slow and cautious titration of metformin dosage is recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
