INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1569849, 'Chromium picolinate', 'Liotrix', 'Moderate', 'Concurrent administration of chromium picolinate-containing oral products may decrease the oral bioavailability of levothyroxine in some patients.  The exact mechanism has not been elucidated but may involve nonspecific adsorption of levothyroxine to polyvalent cations, resulting in an insoluble complex that that is poorly absorbed from the gastrointestinal tract.', 'DDInter', 1767317477, 'Some experts recommend separating the times of administration of levothyroxine and chromium picolinate by either taking oral levothyroxine at least 30 minutes before or three to four hours after taking chromium picolinate-containing oral preparations.  Monitoring of serum TSH levels is recommended.  Patients with gastrointestinal or malabsorption disorders may be at a greater risk of developing clinical or subclinical hypothyroidism due to this interaction.', 'Absorption', 'Some experts recommend separating the times of administration of levothyroxine and chromium picolinate by either taking oral levothyroxine at least 30 minutes before or three to four hours after taking chromium picolinate-containing oral preparations.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1569850, 'Fluticasone', 'Somapacitan', 'Moderate', 'Coadministration with human growth hormone may decrease the plasma concentrations of drugs that are metabolized by CYP450 3A4.  Data from an interaction study performed in growth hormone deficient adults suggest that growth hormone (somatropin) administration can increase the clearance of compounds known to be metabolized by cytochrome P450 isoenzymes, especially CYP450 3A4.', 'DDInter', 1767317477, 'Caution is advised when human growth hormone is used concurrently with drugs that are known CYP450 3A4 substrates, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever human growth hormone is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when human growth hormone is used concurrently with drugs that are known CYP450 3A4 substrates, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
