INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1569852, 'Brentuximab vedotin', 'Lovastatin', 'Moderate', 'Brentuximab vedotin can cause peripheral neuropathy, and concurrent use of other agents that are also associated with this adverse effect can potentiate the risk.', 'DDInter', 1767317477, 'The potential for increased risk and/or severity of peripheral neuropathy and hepatotoxicity should be considered when brentuximab vedotin is used with other drugs that are also associated with these adverse effects.  Patients should be closely monitored for symptoms of neuropathy such as burning, tingling, pain, numbness, or weakness.  Liver enzymes and bilirubin should be measured before and during treatment, especially in patients with underlying hepatic disease or marked baseline transaminase elevations.  Patients experiencing new, worsening, or recurrent neuropathy or hepatotoxicity may require a delay, change in dosage, or discontinuation of brentuximab vedotin in accordance with the product labeling.  Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Synergism', 'The potential for increased risk and/or severity of peripheral neuropathy and hepatotoxicity should be considered when brentuximab vedotin is used with other drugs that are also associated with these adverse effects.', NULL, NULL, 'Moderate', 'pharmacodynamic');
