INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1569971, 'Cobimetinib', 'Netupitant', 'Major', 'Coadministration with inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of cobimetinib, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'Concomitant use of cobimetinib with moderate CYP450 3A4 inhibitors should be avoided whenever possible.  If short-term (14 days or less) use of a moderate CYP450 3A4 inhibitor is required during treatment with cobimetinib 60 mg once daily, the manufacturer suggests reducing the cobimetinib dose to 20 mg.  Following discontinuation of the moderate CYP450 3A4 inhibitor, the previous dose of 60 mg may be resumed.  An alternative to the moderate CYP450 3A4 inhibitor should be used in patients who are already taking a reduced dose of cobimetinib (40 or 20 mg daily).', 'Metabolism', 'Concomitant use of cobimetinib with moderate CYP450 3A4 inhibitors should be avoided whenever possible.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1569972, 'Colchicine', 'Netupitant', 'Major', 'Coadministration with inhibitors of CYP450 3A4 may significantly increase the serum concentrations of colchicine, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'Caution is advised if colchicine is prescribed in combination with moderate CYP450 3A4 inhibitors.  In patients with normal renal and hepatic function, the dosage of colchicine should be reduced when used with moderate CYP450 3A4 inhibitors or within 14 days of using them.  For the treatment of acute gout flares, the adjusted dosage recommended is 1.2 mg for one dose.  Administration should not be repeated for at least three days.  For the prophylaxis of gout flares, the adjusted dosage should be 0.3 mg twice a day (or 0.6 mg once a day) if the original regimen was 0.6 mg twice a day, and 0.3 mg once a day if the original regimen was 0.6 once a day.  For the treatment of familial Mediterranean fever, the maximum dosage of colchicine is 1.2 mg/day (may be given as 0.6 mg twice a day) when used in the presence of moderate CYP450 3A4 inhibitors.  Other significant inhibitors of CYP450 3A4 include amiodarone, dronedarone, imatinib, posaconazole, and quinupristin-dalfopristin, although the extent to which they may interact with colchicine is unknown.  A similar dosage adjustment may be required.  Patients should be advised to contact their physician if they experience symptoms of toxicity such as abdominal pain, nausea, vomiting, diarrhea, fatigue, myalgia, asthenia, hyporeflexia, paresthesia, and numbness.', 'Metabolism', 'Caution is advised if colchicine is prescribed in combination with moderate CYP450 3A4 inhibitors.', NULL, NULL, 'Major', 'pharmacodynamic');
