INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1569976, 'Copanlisib', 'Netupitant', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 and P-glycoprotein (P-gp) may increase the plasma concentrations of copanlisib, which is a substrate of both the isoenzyme and efflux transporter.', 'DDInter', 1767317477, 'Caution is advised when copanlisib is used with CYP450 3A4 inhibitors.  Patients should be monitored for potentially increased adverse effects such as nausea, vomiting, diarrhea, stomatitis, hyperglycemia, hypertension, noninfectious pneumonitis, cutaneous reactions (e.g., exfoliative dermatitis, maculopapular rash), anemia, neutropenia, thrombocytopenia, and infections.  Dosage adjustments for copanlisib may be necessary and should be done in accordance with the product labeling.', 'Metabolism', 'Caution is advised when copanlisib is used with CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1569977, 'Crizotinib', 'Netupitant', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of crizotinib, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'Caution is advised if crizotinib is prescribed in combination with moderate CYP450 3A4 inhibitors.  Regular monitoring of ECG and serum electrolytes, including potassium, magnesium and calcium, may be appropriate in some patients.  Treatment should be interrupted in patients who develop Grade 3 QTc prolongation until recovery to less than or equal to Grade 1, then resumed at 200 mg twice daily.  In case of recurrence of Grade 3 QTc prolongation, therapy should be withheld until recovery to less than or equal to Grade 1, then resumed at 250 mg once daily.  Permanently discontinue crizotinib therapy if Grade 3 QTc prolongation recurs or at any time during treatment if Grade 4 QTc prolongation develops.  Patients should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, palpitations, irregular heartbeat, shortness of breath, or syncope.', 'Metabolism', 'Caution is advised if crizotinib is prescribed in combination with moderate CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
