INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1570323, 'Fosnetupitant', 'Escitalopram', 'Minor', 'Coadministration with inhibitors of CYP450 3A4 is not expected to significantly affect the pharmacokinetics of citalopram or escitalopram, both of which are partially metabolized by the isoenzyme.', 'DDInter', 1767317477, 'Coadministration with inhibitors of CYP450 3A4 is not expected to significantly affect the pharmacokinetics of citalopram or escitalopram, both of which are partially metabolized by the isoenzyme.  Administration of racemic citalopram (40 mg) in combination with the potent CYP450 3A4 inhibitor ketoconazole (200 mg) decreased the ketoconazole peak plasma concentration (Cmax) by 21% and systemic exposure (AUC) by 10%, but did not significantly affect the pharmacokinetics of citalopram.  Likewise, coadministration of a single 20 mg dose of escitalopram with a single 600 mg dose of ritonavir, another potent CYP450 3A4 inhibitor, did not affect the pharmacokinetics of either drug.  Because citalopram and escitalopram are both metabolized by multiple isoenzymes (CYP450 2C19, 3A4, and 2D6), inhibition of a single isoenzyme may not appreciably decrease their clearance.', 'Metabolism', 'Coadministration with inhibitors of CYP450 3A4 is not expected to significantly affect the pharmacokinetics of citalopram or escitalopram, both of which are partially metabolized by the isoenzyme.', NULL, NULL, 'Minor', 'pharmacodynamic');
