INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1570372, 'Ivabradine', 'Fosnetupitant', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of ivabradine, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'Concomitant use of ivabradine with moderate CYP450 3A4 inhibitors should generally be avoided.  If coadministration is required, some authorities recommend initiating ivabradine at a dosage of 2.5 mg twice daily, provided the resting heart rate is at or above 70 beats per minute and heart rate is closely monitored.', 'Metabolism', 'Concomitant use of ivabradine with moderate CYP450 3A4 inhibitors should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1570373, 'Ivacaftor', 'Fosnetupitant', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of ivacaftor, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'ADJUST DOSE:  Coadministration with moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of ivacaftor, which is primarily metabolized by the isoenzyme.  In study subjects, ivacaftor peak plasma concentration (Cmax) and systemic exposure (AUC) increased by approximately 2.5- and 3.0-fold when it was administered concomitantly with fluconazole, a moderate CYP450 3A4 inhibitor.  When lumacaftor/ivacaftor was coadministered with ciprofloxacin, another moderate CYP450 3A4 inhibitor, lumacaftor Cmax and AUC decreased by 12% and 14%, respectively, while ivacaftor Cmax and AUC increased by 29% each.  These changes are not considered clinically significant.  Physiologically based pharmacokinetic (PBPK) simulations suggest that coadministration with moderate CYP450 3A4 inhibitors may increase elexacaftor AUC by 1.9- to 2.3-fold and tezacaftor AUC by approximately 2.1-fold.', 'Metabolism', 'ADJUST DOSE:  Coadministration with moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of ivacaftor, which is primarily metabolized by the isoenzyme.', NULL, NULL, 'Major', 'pharmacodynamic');
