INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1570376, 'Fosnetupitant', 'Ixabepilone', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of ixabepilone, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'Caution is advised when ixabepilone is prescribed with CYP450 3A4 inhibitors.  Pharmacologic response to ixabepilone should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the ixabepilone dosage adjusted as necessary.  Frequent monitoring of peripheral blood counts between treatment cycles is recommended, and patients should be advised to contact their physician if they experience signs and symptoms of ixabepilone toxicities such as infection or burning, tingling, or numbness in the hands and feet.', 'Metabolism', 'Caution is advised when ixabepilone is prescribed with CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
