INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1570402, 'Fosnetupitant', 'Methadone', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of methadone, which is metabolized primarily by CYP450 3A4, 2B6, 2C19, and to a lesser extent by CYP450 2C9 and 2D6.', 'DDInter', 1767317477, 'Caution is advised if methadone is prescribed in combination with CYP450 3A4 inhibitors.  Pharmacologic response to methadone should be closely monitored and the dosage adjusted accordingly, particularly following initiation or discontinuation of the CYP450 3A4 inhibitor in patients who are stabilized on their methadone regimen.  Patients should be advised to report excessive drowsiness, nausea, or asthenia to their physician, and to seek immediate medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.  ECG monitoring should be considered for patients on methadone with heart or liver disease; conduction abnormalities; electrolyte disturbances (i.e., hypokalemia, hypomagnesemia); concomitant use of drugs that may cause QT prolongation or electrolyte loss; concomitant use of CYP450 3A4 inhibitors; or use of methadone at dosages greater than 200 mg daily.', 'Metabolism', 'Caution is advised if methadone is prescribed in combination with CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1570403, 'Fosnetupitant', 'Methylergometrine', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of ergot derivatives, which are primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'Caution is advised during concomitant use of ergot derivatives with mild or moderate CYP450 3A4 inhibitors.  Patients should be monitored for potential ergot toxicity including peripheral vasospasm, ischemia, thrombosis, tachycardia, and hypertension.', 'Metabolism', 'Caution is advised during concomitant use of ergot derivatives with mild or moderate CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
