INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1570518, 'Fosnetupitant', 'Vardenafil', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 and/or 2C9 may increase the plasma concentrations of vardenafil, which is primarily metabolized by the former and secondarily by the latter.', 'DDInter', 1767317477, 'Dosage adjustments may be appropriate for vardenafil whenever a CYP450 3A4 and/or 2C9 inhibitor is added to or withdrawn from therapy based on efficacy and patient tolerance.  The manufacturer recommends that an initial vardenafil dosage of 2.5 to 5 mg be used in patients treated concomitantly with a potent CYP450 3A4 inhibitor such as erythromycin, itraconazole, ketoconazole, indinavir, and ritonavir.  No specific dosage recommendations are available for coadministration with less potent inhibitors.  Patients should be advised to promptly notify their doctor if they experience pain or tightness in the chest or jaw, irregular heartbeat, nausea, shortness of breath, visual disturbances, syncope, or prolonged erection (greater than 4 hours).', 'Metabolism', 'Dosage adjustments may be appropriate for vardenafil whenever a CYP450 3A4 and/or 2C9 inhibitor is added to or withdrawn from therapy based on efficacy and patient tolerance.', NULL, NULL, 'Moderate', 'pharmacodynamic');
