        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1570936, 'Duloxetine', 'Vilanterol', 'Moderate', 'Additive or synergistic effects on blood pressure and heart rate may occur when serotonin-norepinephrine reuptake inhibitors (SNRIs) are combined with sympathomimetic agents such as nasal decongestants, adrenergic bronchodilators, ophthalmic vasoconstrictors, and systemic vasopressors.', NULL, 'Caution is advised if SNRIs are used with other drugs that can increase blood pressure and/or heart rate.', NULL, 'Caution is advised if SNRIs are used with other drugs that can increase blood pressure and/or heart rate.  Blood pressure and pulse should be measured prior to initiating SNRI therapy and monitored at regular intervals thereafter.  Dose reduction or discontinuation of the SNRI should be considered in patients who experience a sustained increase in blood pressure or pulse rate.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
