        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1571252, 'Atorvastatin', 'Norgestimate', 'Minor', 'Coadministration with atorvastatin may increase the plasma concentrations of estrogens and progestins.  The exact mechanism of interaction is unknown but may be related to competitive inhibition of CYP450 3A4, the isoenzyme responsible for the metabolism of atorvastatin, estrogens, some (if not all) progestins, and other steroids.', NULL, 'The clinical significance of these alterations is unknown but should be considered when selecting oral contraceptive and hormone replacement doses.', NULL, 'The clinical significance of these alterations is unknown but should be considered when selecting oral contraceptive and hormone replacement doses.', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
