        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1571269, 'Cholestyramine', 'Norgestimate', 'Moderate', 'Coadministration with bile-acid binding resins such as cholestyramine may decrease the plasma concentrations and therapeutic effects of estrogens and progestins.  The proposed mechanism involves delaying or reducing the absorption of concomitant orally administered drugs by cholestyramine.', NULL, 'Estrogens and/or progestin-containing oral medications should be administered at least one hour before or four to six hours after bile-acid binding resins such as cholestyramine, colestipol, and colesevelam.', NULL, 'Estrogens and/or progestin-containing oral medications should be administered at least one hour before or four to six hours after bile-acid binding resins such as cholestyramine, colestipol, and colesevelam.  Patients receiving this combination should be advised to closely follow dosing schedules to prevent loss of therapeutic effects.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
