    ) VALUES (1571371, 'Norgestimate', 'Nelfinavir', 'Moderate', 'Nelfinavir may decrease the plasma concentrations of contraceptive hormones during chronic coadministration.  The proposed mechanism is nelfinavir induction of the hepatic metabolism of sex hormones.', NULL, 'When using Nelfinavir and birth control pills together, we recommend additional contraception.', NULL, 'When using Nelfinavir and birth control pills together, we recommend additional contraception.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
INSERT OR IGNORE INTO drug_interactions (
