    ) VALUES (1571463, 'Desogestrel', 'Asparaginase Erwinia chrysanthemi', 'Moderate', 'Coadministration with another thiazolidinedione, troglitazone, has been shown to decrease the plasma concentrations of contraceptive hormones.  The proposed mechanism is induction of hormone metabolism via CYP450 3A4.', NULL, 'The use of oral contraceptives during asparaginase treatment is not recommended.', NULL, 'The use of oral contraceptives during asparaginase treatment is not recommended.  Other non-oral contraceptive methods should be used in women of childbearing potential.  According to the manufacturer, females of reproductive potential should be advised to use effective contraception during asparaginase treatment and for at least 3 to 6 months after the last dose.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
