    ) VALUES (1571465, 'Desogestrel', 'Atazanavir', 'Moderate', 'Coadministration with atazanavir may increase the plasma concentrations of ethinyl estradiol and norethindrone and possibly other contraceptive hormones.  The mechanism is atazanavir inhibition of CYP450 3A4 and UGT1A1, the enzymes responsible for the metabolic clearance of contraceptive hormones.', NULL, 'The manufacturer recommends alternate methods of nonhormonal contraception during atazanavir or atazanavir-ritonavir therapy.', NULL, 'The manufacturer recommends alternate methods of nonhormonal contraception during atazanavir or atazanavir-ritonavir therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
