        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1571488, 'Desogestrel', 'Clozapine', 'Moderate', 'Limited data suggest that coadministration with contraceptive hormones may increase the plasma concentrations of clozapine.  The exact mechanism of interaction is unknown but may involve inhibition of clozapine metabolism via CYP450 1A2, 2C19 and/or 3A4.', NULL, 'Patients treated concomitantly with hormonal contraceptives may require a lower dosage of clozapine to control psychotic symptoms.', NULL, 'Patients treated concomitantly with hormonal contraceptives may require a lower dosage of clozapine to control psychotic symptoms.  Pharmacologic response to clozapine should be monitored more closely whenever an oral contraceptive is added to or withdrawn from therapy in patients stabilized on their existing antipsychotic regimen, and the clozapine dosage adjusted as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
