    ) VALUES (1571490, 'Desogestrel', 'Colesevelam', 'Moderate', 'Colesevelam may decrease the gastrointestinal absorption of contraceptive hormones.', NULL, 'Oral contraceptives containing ethinyl estradiol and norethindrone should be administered at least 4 hours before the colesevelam dose.', NULL, 'Oral contraceptives containing ethinyl estradiol and norethindrone should be administered at least 4 hours before the colesevelam dose.  The same precaution may be applicable to other contraceptive hormones, although data are lacking.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
