    ) VALUES (1571497, 'Desogestrel', 'Deferasirox', 'Moderate', 'Coadministration with deferasirox may decrease the plasma concentrations of contraceptive hormones.  The proposed mechanism is deferasirox induction of CYP450 3A4, the isoenzyme primarily responsible for the metabolic clearance of sex hormones.', NULL, 'Hormonal contraceptives agents may not be reliable when used with deferasirox.', NULL, 'Hormonal contraceptives agents may not be reliable when used with deferasirox.  Female patients of reproductive potential receiving hormonal contraceptives should be counseled to use alternative or additional methods of birth control during treatment with deferasirox.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
