    ) VALUES (1571513, 'Erythromycin', 'Desogestrel', 'Moderate', 'The effectiveness of estrogen-containing oral contraceptives may be impaired by concomitant treatment with antimicrobial agents.', NULL, 'During concomitant therapy with erythromycin or clarithromycin, patients should be observed for increased or altered pharmacologic response to estrogens and progestogens, and dosage(s) adjusted accordingly as necessary.', NULL, 'During concomitant therapy with erythromycin or clarithromycin, patients should be observed for increased or altered pharmacologic response to estrogens and progestogens, and dosage(s) adjusted accordingly as necessary.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
