    ) VALUES (1571562, 'Desogestrel', 'Linaclotide', 'Moderate', 'The absorption and efficacy of oral contraceptives may be reduced if severe diarrhea occurs with linaclotide treatment.  Linaclotide has been associated with severe and prolonged diarrhea.', NULL, 'In patients who develop severe diarrhea during treatment with linaclotide, European labeling recommends use of an additional contraceptive method to prevent possible failure of oral contraception.', NULL, 'In patients who develop severe diarrhea during treatment with linaclotide, European labeling recommends use of an additional contraceptive method to prevent possible failure of oral contraception.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
