    ) VALUES (1571578, 'Desogestrel', 'Morphine', 'Minor', 'Oral contraceptives induce glucuronidation and may decrease plasma concentrations of morphine, diamorphine, and other opioids.', NULL, 'Oral contraceptives may influence opioid analgesic effects and opioid dosage may need to be adjusted.', NULL, 'Oral contraceptives may influence opioid analgesic effects and opioid dosage may need to be adjusted.', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
