        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1571648, 'Desogestrel', 'Tranexamic acid', 'Major', 'Because tranexamic acid is an antifibrinolytic agent, concomitant use may further exacerbate the risk of thrombotic events, including venous thromboembolism as well as arterial thromboses such as stroke and myocardial infarction, associated with hormonal contraceptives.', NULL, 'Due to the potential for increased risk of thrombosis in women receiving hormonal contraceptives, the use of tranexamic acid for the treatment of menorrhagia in this population is considered contraindicated.', NULL, 'Due to the potential for increased risk of thrombosis in women receiving hormonal contraceptives, the use of tranexamic acid for the treatment of menorrhagia in this population is considered contraindicated.  Patients receiving hormonal contraceptives should be advised to seek medical attention immediately if they experience potential signs and symptoms of blood clots such as chest pain, shortness of breath, hemoptysis, hematuria, sudden loss of vision, and pain, redness or swelling in an extremity.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
