INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1571825, 'Lumefantrine', 'Lefamulin', 'Major', 'When administered orally, lefamulin may significantly increase the plasma concentrations of drugs that are primarily metabolized by CYP450 3A4.  Increased exposure to sensitive CYP450 3A4 substrates that can prolong the QT interval may increase the risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 1767317477, 'Concomitant use of oral (but not intravenous) lefamulin with sensitive CYP450 3A4 substrates that can prolong the QT interval is considered contraindicated.  However, because lefamulin itself can also cause QT prolongation regardless of the route of administration, concomitant use with other drugs that can prolong the QT interval should generally be avoided even when lefamulin is administered intravenously.', 'Metabolism', 'Concomitant use of oral (but not intravenous) lefamulin with sensitive CYP450 3A4 substrates that can prolong the QT interval is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
