INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1571871, 'Nevirapine', 'Artemether', 'Major', 'Coadministration of artemether-lumefantrine with the nonnucleoside reverse transcriptase inhibitors efavirenz, etravirine, or nevirapine may result in decreased plasma concentrations of one or both drugs.  These drugs are all primarily metabolized by CYP450 3A4 but are also inducers of the isoenzyme.', 'DDInter', 1767317477, 'Patients should be closely monitored for reduced antimalarial efficacy if artemether-lumefantrine must be used in combination with a nonnucleoside reverse transcriptase inhibitor.  Food consumption should be encouraged at dosing times to compensate for the decrease in artemether-lumefantrine bioavailability.  Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, close clinical and laboratory monitoring of antiretroviral response is also recommended.', 'Metabolism', 'Patients should be closely monitored for reduced antimalarial efficacy if artemether-lumefantrine must be used in combination with a nonnucleoside reverse transcriptase inhibitor.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1571872, 'Nevirapine', 'Lumefantrine', 'Moderate', 'Coadministration with nevirapine may decrease the plasma concentrations and therapeutic efficacy of drugs that are substrates of CYP450 3A4.  The proposed mechanism is increased clearance due to nevirapine-mediated induction of CYP450 3A4.', 'DDInter', 1767317477, 'Caution is advised if nevirapine is used concomitantly with drugs that are CYP450 3A4 substrates.  Dosage adjustments as well as clinical and laboratory monitoring of coadministered drugs should be considered whenever nevirapine is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if nevirapine is used concomitantly with drugs that are CYP450 3A4 substrates.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1571873, 'Nilotinib', 'Lumefantrine', 'Major', 'Artemether-lumefantrine may cause prolongation of the QT interval.  Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 1767317477, 'Coadministration of artemether-lumefantrine with other drugs that can prolong the QT interval should generally be avoided.  Caution is recommended when these drugs are used following treatment with artemether-lumefantrine due to the long elimination half-life of lumefantrine (3 to 6 days).  Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Coadministration of artemether-lumefantrine with other drugs that can prolong the QT interval should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
