INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1571997, 'Thioridazine', 'Lumefantrine', 'Major', 'Coadministration with inhibitors of CYP450 2D6 may increase the plasma concentrations of thioridazine and its two active metabolites, mesoridazine and sulforidazine, all of which are substrates of the isoenzyme.', 'DDInter', 1767317477, 'The use of thioridazine with drugs that inhibit CYP450 2D6 is considered contraindicated.  Depending on the elimination half-life of these drugs, a considerable waiting period may be appropriate following their discontinuation before thioridazine is initiated.  For example, the manufacturer of fluoxetine recommends that thioridazine not be administered within 5 weeks after discontinuing fluoxetine because of the drug''s long half-life.  In addition, the prolonged duration of CYP450 2D6 inhibition by the moderate CYP450 2D6 inhibitor rolapitant of at least 28 days after its administration should also be taken into account.', 'Metabolism', 'The use of thioridazine with drugs that inhibit CYP450 2D6 is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1571998, 'Timolol', 'Lumefantrine', 'Moderate', 'Coadministration with lumefantrine may increase the plasma concentrations of drugs that are substrates of the CYP450 2D6 isoenzyme.', 'DDInter', 1767317477, 'Caution is advised if lumefantrine is used concurrently with medications that undergo metabolism by CYP450 2D6, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever lumefantrine is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if lumefantrine is used concurrently with medications that undergo metabolism by CYP450 2D6, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1571999, 'Tizanidine', 'Lumefantrine', 'Major', 'Artemether-lumefantrine may cause prolongation of the QT interval.  Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 1767317477, 'Coadministration of artemether-lumefantrine with other drugs that can prolong the QT interval should generally be avoided.  Caution is recommended when these drugs are used following treatment with artemether-lumefantrine due to the long elimination half-life of lumefantrine (3 to 6 days).  Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Coadministration of artemether-lumefantrine with other drugs that can prolong the QT interval should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1572000, 'Tolterodine', 'Lumefantrine', 'Minor', 'Coadministration with drugs that are inhibitors of CYP450 2D6 may increase the plasma concentrations of tolterodine, which is primarily metabolized by this isoenzyme in patients that are extensive metabolizers, or EMs (approximately 93% of Caucasians and more than 98% of Asians and individuals of African descent).', 'DDInter', 1767317477, 'This interaction is unlikely to produce clinically significant effects because of metabolic shunting to CYP450 3A4 in 2D6-deficient states.  Moreover, since the active 5-hydroxymethyl metabolite (5-HM) generated by CYP450 2D6 is equipotent compared with tolterodine, and the sum of unbound serum concentrations of tolterodine and 5-HM are only 25% higher during the interaction, little alteration in the overall pharmacologic activity would be expected.  Thus, dosage reductions should not be necessary.  However, a prolonged duration of monitoring for adverse effects may be required depending on the elimination half-life of the concomitant drug.  For example, it should be noted that rolapitant, a moderate CYP450 2D6 inhibitor, can increase plasma concentrations and the risk of adverse effects of tolterodine for at least 28 days after administration of rolapitant.', 'Metabolism', 'This interaction is unlikely to produce clinically significant effects because of metabolic shunting to CYP450 3A4 in 2D6-deficient states.', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1572001, 'Toremifene', 'Lumefantrine', 'Major', 'Artemether-lumefantrine may cause prolongation of the QT interval.  Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 1767317477, 'Coadministration of artemether-lumefantrine with other drugs that can prolong the QT interval should generally be avoided.  Caution is recommended when these drugs are used following treatment with artemether-lumefantrine due to the long elimination half-life of lumefantrine (3 to 6 days).  Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Coadministration of artemether-lumefantrine with other drugs that can prolong the QT interval should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1572002, 'Tramadol', 'Lumefantrine', 'Major', 'Artemether-lumefantrine and tramadol may each cause prolongation of the QT interval.  Theoretically, coadministration of multiple agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 1767317477, 'Coadministration of artemether-lumefantrine with tramadol should generally be avoided.  If concomitant use is required, careful consideration of the effects on tramadol and M1 is recommended.  Patients should be monitored for opioid withdrawal, seizures, and serotonin syndrome, and care should be exercised in patients suspected to be at an increased risk of torsade de pointes.  If artemether-lumefantrine is discontinued, consider reducing the tramadol dose (taking into account the elimination half-life of lumefantrine [3 to 6 days]) until stable drug effects are achieved and monitor for respiratory depression and sedation.  Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Coadministration of artemether-lumefantrine with tramadol should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
