INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1572011, 'Troleandomycin', 'Artemether', 'Major', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of artemether and lumefantrine, both of which are primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'No dose adjustment of artemether-lumefantrine is necessary when administered in combination with potent CYP450 3A4 inhibitors such as azole antifungal agents, nefazodone, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics.  However, caution is advised due to increased risk of QT interval prolongation.  Patients should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, palpitations, or syncope.  Patients should also be monitored for reduced effects or potential loss of efficacy of the concomitant drug(s), especially antiretroviral agents.', 'Metabolism', 'No dose adjustment of artemether-lumefantrine is necessary when administered in combination with potent CYP450 3A4 inhibitors such as azole antifungal agents, nefazodone, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics.', NULL, NULL, 'Major', 'pharmacodynamic');
