INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1572310, 'Amikacin', 'Phenyl salicylate', 'Moderate', 'The nephrotoxic effect of aminoglycosides may be potentiated by nonsteroidal anti-inflammatory drugs (NSAIDs), particularly if the latter had been given in high dosages for prolonged periods.', 'DDInter', 1767317477, 'It may be advisable to consider reducing the dosage of aminoglycoside prior to initiation of NSAID therapy in infants.  During coadministration, plasma antibiotic concentrations and renal function should be closely monitored, and the antibiotic dosage further adjusted as necessary.', 'Synergism', 'It may be advisable to consider reducing the dosage of aminoglycoside prior to initiation of NSAID therapy in infants.', NULL, NULL, 'Moderate', 'pharmacodynamic');
