INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1572320, 'Baricitinib', 'Phenyl salicylate', 'Moderate', 'Coadministration of Janus kinase (JAK) inhibitors with corticosteroids, nonsteroidal anti-inflammatory drugs (NSAIDs), and/or opioids may increase the risk of gastrointestinal (GI) perforation.', 'DDInter', 1767317477, 'Caution is recommended when using JAK inhibitors in patients with a history of peptic ulceration or diverticular disease and in patients receiving concomitant treatment with drugs associated with an increased risk of GI perforation such as corticosteroids, NSAIDs, and opioids.  Patients should be advised to contact their healthcare provider if they experience signs and symptoms of GI perforation such as severe abdominal pain, fever, chills, nausea, or vomiting.', 'Synergism', 'Caution is recommended when using JAK inhibitors in patients with a history of peptic ulceration or diverticular disease and in patients receiving concomitant treatment with drugs associated with an increased risk of GI perforation such as corticosteroids, NSAIDs, and opioids.', NULL, NULL, 'Moderate', 'pharmacodynamic');
