INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1572368, 'Epoprostenol', 'Phenyl salicylate', 'Moderate', 'Prostacyclin/prostacyclin analogs (e.g., epoprostenol, iloprost, treprostinil) inhibit platelet aggregation and may theoretically potentiate the risk of bleeding in patients treated with other agents that affect hemostasis such as anticoagulants, platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia.', 'DDInter', 1767317477, 'Caution is advised if prostacyclin and prostacyclin analogs are used in combination with other drugs that affect hemostasis.  Close clinical and laboratory observation for hemorrhagic complications is recommended.  Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Caution is advised if prostacyclin and prostacyclin analogs are used in combination with other drugs that affect hemostasis.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1572369, 'Eprosartan', 'Phenyl salicylate', 'Moderate', 'Nonsteroidal anti-inflammatory drugs (NSAIDs) may attenuate the antihypertensive effects of angiotensin II receptor antagonists.  The proposed mechanism is NSAID-induced inhibition of renal prostaglandin synthesis, which results in unopposed pressor activity producing hypertension.  In addition, NSAIDs can cause fluid retention, which also affects blood pressure.', 'DDInter', 1767317477, 'Patients receiving angiotensin II receptor antagonists who require prolonged (greater than 1 week) concomitant therapy with an NSAID should have blood pressure monitored more closely following initiation, discontinuation, or change of dosage of the NSAID.  Renal function should also be evaluated periodically during prolonged coadministration.  The interaction is not expected to occur with low doses (e.g., low-dose aspirin) or intermittent short-term administration of NSAIDs.', 'Antagonism', 'Patients receiving angiotensin II receptor antagonists who require prolonged (greater than 1 week) concomitant therapy with an NSAID should have blood pressure monitored more closely following initiation, discontinuation, or change of dosage of the NSAID.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1572370, 'Eptifibatide', 'Phenyl salicylate', 'Moderate', 'Although aspirin is routinely given with glycoprotein IIb/IIIa inhibitors, be cognizant of the increased risk of bleeding associated with this combination due to the additive inhibitory effects of salicylates on platelet function.  Arterial puncture sites may be most problematic.', 'DDInter', 1767317477, 'It is recommended that these drugs be used together only when indicated by skilled personnel who are qualified and equipped to detect and treat bleeding complications.  Patients should be closely monitored for signs of bleeding, especially at arterial puncture sites.  Therapy should be discontinued immediately if serious or uncontrollable bleeding or thrombocytopenia occurs.', 'Synergism', 'It is recommended that these drugs be used together only when indicated by skilled personnel who are qualified and equipped to detect and treat bleeding complications.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1572371, 'Erlotinib', 'Phenyl salicylate', 'Moderate', 'Coadministration of erlotinib with anti-angiogenic agents, corticosteroids, nonsteroidal anti-inflammatory drugs (NSAIDs), and/or taxane-based chemotherapy may increase the risk of gastrointestinal (GI) perforation.', 'DDInter', 1767317477, 'Caution is recommended when using erlotinib in patients with a history of peptic ulceration or diverticular disease and in patients receiving concomitant treatment with drugs associated with an increased risk of GI perforation such as anti-angiogenic agents, corticosteroids, NSAIDs, and taxane-based chemotherapy.  Patients should be advised to contact their healthcare provider if they experience signs and symptoms of GI perforation such as severe abdominal pain, fever, chills, nausea, or vomiting.  The manufacturer recommends that erlotinib should be permanently discontinued in patients who develop gastrointestinal perforation.', 'Synergism', 'Caution is recommended when using erlotinib in patients with a history of peptic ulceration or diverticular disease and in patients receiving concomitant treatment with drugs associated with an increased risk of GI perforation such as anti-angiogenic agents, corticosteroids, NSAIDs, and taxane-based chemotherapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
