    ) VALUES (1572445, 'Phenyl salicylate', 'Omeprazole', 'Minor', 'Coadministration with proton pump inhibitors may decrease the oral bioavailability of aspirin and other salicylates.', NULL, 'In clinical practice, proton pump inhibitors are frequently used to attenuate gastric mucosal damage caused by salicylates.', NULL, 'In clinical practice, proton pump inhibitors are frequently used to attenuate gastric mucosal damage caused by salicylates. It is only necessary to pay attention to the dosing interval.', 'Absorption', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
