    ) VALUES (1572481, 'Tenecteplase', 'Phenyl salicylate', 'Moderate', 'Drugs that inhibit platelet function may increase the risk of bleeding when administered prior to, during, or after thrombolytic therapy.', NULL, 'Careful monitoring for signs of bleeding, particularly at arterial puncture wounds, is recommended when thrombolytics are used concurrently or sequentially with antiplatelet agents other than aspirin.', NULL, 'Careful monitoring for signs of bleeding, particularly at arterial puncture wounds, is recommended when thrombolytics are used concurrently or sequentially with antiplatelet agents other than aspirin.  Some authorities recommend avoiding the initiation of platelet aggregation inhibitors within the first 24 hours following thrombolysis treatment.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
INSERT OR IGNORE INTO drug_interactions (
